import Modal from "./components/Modal/Modal";
import { useState, useEffect } from "react";
import classes from "./App.module.css";
import Container from "./components/Container/Container";
import Button from "./components/Button/Button";
import TodoCard from "./components/TodoCard/TodoCard";
import Pagination from "./components/Pagination/Pagination";

function App() {

  const limit = 10;
  const BASE_URL = "https://jsonplaceholder.typicode.com/"
  const [isShow, setIsShow] = useState(false);
  const [newTask, setNewTask] = useState("");
  const [search, setSearch] = useState("");
  const [currentEdit, setCurrentEdit] = useState(null);
  const [filter, setFilter] = useState("all");
  const [offset, setOffSet] = useState(1)
  const page = Math.floor(offset / limit) || 1
  const [tasks, setTasks] = useState([]);
  const handleShow = () => setIsShow(!isShow);

  const handleAddTask = () => {
    if (newTask.length < 1) return;

    setTasks((prevState) => [
      ...prevState,
      {
        id: Date.now(),
        title: newTask,
        completed: false,
      },
    ]);
    setNewTask("");
    handleShow();
  };

  const handleDone = (id) => {
    const newList = tasks.map((task) => {
      if (task.id === id) {
        return { ...task, completed: !task.completed };
      } else {
        return task;
      }
    });
    setTasks([...newList]);
  };

  const handleDelete = (id) => {
    const deletedLedList = tasks.filter((task) => task.id !== id);
    setTasks([...deletedLedList]);
  };

  const deleteAll = () => {
    const deleteList = tasks.splice(tasks.length);
    setTasks([...deleteList]);
  };
  const handleSearch = (event) => {
    setSearch(event.target.value);
  };

  const handleEdit = (editTask) => {
    setCurrentEdit(null);
    const editList = tasks.map((task) => {
      if (task.id === editTask.id) {
        return editTask;
      } else {
        return task;
      }
    });
    setTasks([...editList]);
  };

  const fetchData = async(endPoint) => {
    try {
      const data = await fetch(BASE_URL + endPoint)
      console.log(data, 'data');
      const todos = await data.json()
      console.log(todos, 'todos');
      setTasks([...todos])
    } catch (error){
      console.error();
    }
  }

  const handleNext = () => {
    setOffSet(prev => prev + limit)
  }

  const handlePrev = () => {
    if(offset === 1 ) return
    setOffSet(prev => prev - limit)
  }


  const handleCancel = (oldTask) => {
    setCurrentEdit(null);
    const updatedTaskList = tasks.map((task) => {
      if (task.id === oldTask.id) {
        return oldTask;
      } else {
        return task;
      }
    });
    setTasks(updatedTaskList);
  };

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(search.toLowerCase())
  );

  const resultFilter =
    filter === "all"
      ? filteredTasks
      : filter === "completed"
      ? filteredTasks.filter((i) => i.completed)
      : filter === "not completed"
      ? filteredTasks.filter((i) => !i.completed)
      : null;

  const setFiltered = (e) => {
    setFilter(e.target.value);
  };




  useEffect(() => {
    fetchData (`todos?_limit=${limit}&start=${offset}`)
}, [ offset ])

  // useEffect(() => {
  //   const myLocalTasks = JSON.parse(localStorage.getItem("taskList"));
  //   if (myLocalTasks === 0) {
  //     setTasks(myLocalTasks);
  //   }
  // }, []);

  // useEffect(() => {
  //   localStorage.setItem("taskList", JSON.stringify(tasks));
  // }, [tasks]);

  return (
    <>
      <Container>
        <div className={classes.wrapper}>
          {isShow && (
            <Modal
              handleAddTask={handleAddTask}
              setNewTask={setNewTask}
              handleShow={handleShow}
            />
          )}
          <Button handleClick={handleShow}>
            <p>Add</p>
          </Button>
          <input
            className={classes.searchStyle}
            name="search"
            placeholder="Search..."
            onChange={handleSearch}
          />
          <select className={classes.selectStyle} onChange={setFiltered}>
            <option value="all">All tasks</option>
            <option value="completed">Completed</option>
            <option value="not completed">Not completed</option>
          </select>
          <button className={classes.deleteAll} onClick={deleteAll}>
            Delete tasks
          </button>
          {resultFilter.map((task) => (
            <TodoCard
              handleDone={handleDone}
              handleDelete={handleDelete}
              handleSelectEdit={setCurrentEdit}
              handleEdit={handleEdit}
              handleCancel={handleCancel}
              deleteAll={deleteAll}
              isEdit={currentEdit === task.id}
              key={task.id}
              task={task}
            />
          ))}
        </div>
        <Pagination
          handleNext={handleNext}
          page = {page}
          handlePrev={handlePrev}/>
      </Container>
    </>
  );
}

export default App;
